mapper.select:
SELECT proc.id AS process_id, proc.`name` AS process_name, proc.`desc` AS process_desc, proc.cur_task, proc.total_task, proc.`status`, proc.start_date, proc.end_date, colg.`type`, mem.seq, mem.id, mem.id, mem.full_name, mem.nickname, org_dept.`name` AS dept_name, org_rank.`name` AS rank_name, mem.avatar_path FROM tb_agcy_proj_proc AS proc LEFT JOIN tb_agcy_proj_proc_colleague as colg ON proc.id = colg.ref_proc_id LEFT JOIN tb_member AS mem ON mem.seq = colg.ref_colleague_seq LEFT JOIN tb_org_structure AS org_dept ON org_dept.id = mem.dept_no LEFT JOIN tb_org_structure AS org_rank ON org_rank.id = mem.rank_no WHERE proc.delete_yn = #{delete_yn}
AND colg.type = #{colg_type}
AND proc.ref_proj_id = #{ref_proj_id}

SELECT mem.seq, mem.id, mem.avatar_path, org_rank.`name` AS rank_name, org_dept.`name` AS dept_name, mem.full_name, mem.nickname, mem.phone FROM tb_agcy_proj_colleague AS colg LEFT JOIN tb_member AS mem ON colg.ref_agcy_colleague_seq = mem.seq LEFT JOIN tb_org_structure AS org_rank ON org_rank.id = mem.rank_no LEFT JOIN tb_org_structure AS org_dept ON org_dept.id = mem.dept_no
AND colg.ref_proj_id = #{ref_proj_id}
AND colg.delete_yn = "N"

SELECT proc.`name`, proc.`desc`, proc.rating, proc.cur_task, proc.total_task, proc.add_info, proc.start_date, proc.end_date, org_dept.`name` AS dept_name, org_rank.`name` AS rank_name, mem.full_name, mem.followed, mem.avatar_path FROM tb_agcy_proj_proc AS proc LEFT JOIN tb_member AS mem ON mem.seq = proc.writer_seq LEFT JOIN tb_org_structure AS org_dept ON mem.dept_no = org_dept.id LEFT JOIN tb_org_structure AS org_rank ON mem.dept_no = org_rank.id
AND proc.delete_yn = #{delete_yn}
AND proc.id = #{ref_proc_id}

SELECT procColg.`type`, procColg.submit_count, mem.id, mem.full_name, mem.phone, org_dept.`name` AS dept_name, org_rank.`name` AS rank_name, mem.followed, mem.avatar_path FROM tb_agcy_proj_proc_colleague AS procColg LEFT JOIN tb_member AS mem ON mem.seq = procColg.ref_colleague_seq LEFT JOIN tb_org_structure AS org_dept ON org_dept.id = mem.dept_no LEFT JOIN tb_org_structure AS org_rank ON org_rank.id = mem.rank_no
AND procColg.delete_yn = #{delete_yn}
AND procColg.ref_proc_id = #{ref_proc_id}
AND procColg.`type` = #{type}
